import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useNavigate, useLocation, useParams } from "react-router-dom";
import {
  Form,
  Button,
  Row,
  ListGroup,
  Image,
  Card,
  Col,
} from "react-bootstrap";
import Message from "../components/Message";
import Loading from "../components/Loading";
import { getOrderDetails } from "../actions/orderActions";

function OrderScreen() {
  const orderId = useParams().id;
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [sdkReady, setSdkReady] = useState(false);

  const orderDetail = useSelector((state) => state.orderDetails);
  const { order, loading, error } = orderDetail;
  if (!loading && !error) {
    order.itemsPrice = order.orderItems.reduce(
      (acc, item) => acc + item.price * item.qty,
      0
    );
  }
  // AS3xj6leNN-hC_R9MLusXInulu3tpvkAOZjlzHjZ8y5n3pPmOfSDUihzHd7vKfxCjARjqaBKudUbcDHf
{/* <script src="https://www.paypal.com/sdk/js?client-id=BAAlk163sc2h2lQm8qEByWrenNtMVqyMTWU2WK2hVciR64ip1CkJlOT2sUWhxu8j3THpouKl8ToOfbqV78&components=hosted-buttons&disable-funding=venmo&currency=USD"></script>
<div id="paypal-container-TN55BRLX2EUV6"></div>
<script>
  paypal.HostedButtons({
    hostedButtonId: "TN55BRLX2EUV6",
  }).render("#paypal-container-TN55BRLX2EUV6")
</script> */}
const addPaypalScript = () => { 
  const script = document.createElement('script');
  script.type = 'text/javascript';
  script.src = 'https://www.paypal.com/sdk/js?client-id=AS3xj6leNN-hC_R9MLusXInulu3tpvkAOZjlzHjZ8y5n3pPmOfSDUihzHd7vKfxCjARjqaBKudUbcDHf&components=hosted-buttons&disable-funding=venmo&currency=USD';
  script.async = true;
  script.onload = () => {
    setSdkReady(true);
  }
  document.body.appendChild(script);
}
  useEffect(() => {
    if (!order || order._id !== Number(orderId)) {
      dispatch(getOrderDetails(orderId));
    }
  }, [orderId, order, dispatch]);

  return loading ? (
    <Loading />
  ) : error ? (
    <Message variant="danger">{error}</Message>
  ) : (
    <div>
      <h1>Order {order._id}</h1>
      <Row>
        <Col md={8}>
          <ListGroup variant="flush">
            <ListGroup.Item>
              <h2>Shipping</h2>
              <p>
                <strong>Name:</strong>
                {order.user.name}
              </p>
              <p>
                <strong>Email:</strong>
                <a href={`mailto:${order.user.email}`}>{order.user.email}</a>
              </p>
              <p>
                <strong>Address:</strong>
                {order.shippingAddress.address},{order.shippingAddress.city},{" "}
                {order.shippingAddress.postalCode},{" "}
                {order.shippingAddress.country}
              </p>
              {order.isDelivered ? (
                <Message variant="success">
                  Delivered on {order.deliveredAt}
                </Message>
              ) : (
                <Message variant="danger">Not Delivered</Message>
              ) 
              }
            </ListGroup.Item>
            <ListGroup.Item>
              <h2>Payment Method</h2>
              <p>
                <strong>Method:</strong>
                {order.paymentMethod}

              </p>
              {order.isPaid ? (
                <Message variant="success">Delivered on {order.paidAt}</Message>
              ) : (
                <Message variant="danger">Not Paid</Message>
              )}

            </ListGroup.Item>
            <ListGroup.Item>
              <h2>Order Items</h2>
              {order.orderItems.length === 0 ? (
                <Message>Your order is empty</Message>
              ) : (
                <ListGroup variant="flush">
                  {order.orderItems.map((item, index) => (
                    <ListGroup.Item key={index}>
                      <Row>
                        <Col md={1}>
                          <Image
                            src={item.image}
                            alt={item.name}
                            fluid
                            rounded
                          />
                        </Col>
                        <Col>
                          <Link to={`/product/${item.product}`}>
                            {item.name}
                          </Link>
                        </Col>
                        <Col md={4}>
                          {item.qty} x ${item.price} = ${item.qty * item.price}
                        </Col>
                      </Row>
                    </ListGroup.Item>
                  ))}
                </ListGroup>
              )}
            </ListGroup.Item>
          </ListGroup>
        </Col>
        <Col md={4}>
          <Card>
            <ListGroup variant="flush">
              <ListGroup.Item>
                <h2>Order Summary</h2>
              </ListGroup.Item>
              <ListGroup.Item>
                <Row>
                  <Col>Items</Col>
                  <Col>${order.itemsPrice}</Col>
                </Row>
              </ListGroup.Item>
              <ListGroup.Item>
                <Row>
                  <Col>Shipping</Col>
                  <Col>${order.shippingPrice}</Col>
                </Row>
              </ListGroup.Item>
              <ListGroup.Item>
                <Row>
                  <Col>Tax</Col>
                  <Col>${order.taxPrice}</Col>
                </Row>
              </ListGroup.Item>
              <ListGroup.Item>
                <Row>
                  <Col>Total</Col>
                  <Col>${order.totalPrice}</Col>
                </Row>
              </ListGroup.Item>
              {/* {error && <Message variant="danger">{error}</Message>}

              <ListGroup.Item>
                <Button
                  type="button"
                  className="btn-block"
                  disabled={order.orderItems === 0}
                  onClick={placeOrderHandler}
                >
                  Place Order
                </Button>
              </ListGroup.Item> */}
            </ListGroup>
          </Card>
        </Col>
      </Row>
    </div>
  );
}

export default OrderScreen;
